import React from 'react';
import { saveAs } from 'file-saver'; // For PDF downloads

// Import your images (replace with actual paths)
import form1Img from '../assets/BankingForms/form1.jpg';
import form2Img from '../assets/BankingForms/form2.jpg';
import form3Img from '../assets/BankingForms/form3.jpg';

const BankingForms = () => {
  // Sample form data
  const forms = [
    {
      id: 1,
      title: "Account Opening Form",
      image: form1Img,
      pdfUrl: "/pdfs/account-opening-form.pdf"
    },
    {
      id: 2,
      title: "Loan Application Form",
      image: form2Img,
      pdfUrl: "/pdfs/loan-application.pdf"
    },
    {
      id: 3,
      title: "KYC Update Form",
      image: form3Img,
      pdfUrl: "/pdfs/kyc-update.pdf"
    },
    // Add more forms as needed
  ];

  // Handle PDF download
  const handleDownload = (pdfUrl, title) => {
    saveAs(pdfUrl, `${title}.pdf`);
  };

  return (
    <div className="BankingForms-container">
      <h1 className="page-title">Banking Forms</h1>
      
      <div className="forms-scroller">
        {forms.map((form) => (
          <div key={form.id} className="form-card">
            <div className="form-image-container">
              <img 
                src={form.image} 
                alt={form.title} 
                className="form-image"
              />
            </div>
            <h3 className="form-title">{form.title}</h3>
            <button
              onClick={() => handleDownload(form.pdfUrl, form.title)}
              className="download-btn"
            >
              Download PDF
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BankingForms;
